/* ==UserStyle==
@name           fjacosta_mx Oracle Confluence Dracula Theme
@namespace      github.com/openstyles/stylus
@version        1.0.0
@description    A new userstyle
@author         Me
@var color bg-color "BG color" #282a36
@var color fg-color "FG color" #f8f8f2
@var color dracu-current "Dracula current" #44475a
@var color dracu-comment "Dracula comment" #6272a4
@var color dracu-selection "Dracula selection" #44475a
@var color dracu-purple "Dracula Purple" #bd93f9
@var color dracu-orange "Dracula Orange" #ffb86c
@var color dracu-pink "Dracula Pink" #ff79c6
@var color dracu-cyan "Dracula Cyan" #8be9fd
@var color dracu-yellow "Dracula Yellow" #f1fa8c

==/UserStyle== */

@-moz-document regexp("https://confluence.oraclecorp.com/.+") {
    .aui-page-panel, #header .aui-header,
    #main #main-header.overlay-header,
    .theme-default .ia-splitter #footer,
    #footer-logo {
        background: var(--bg-color);
        background-color: var(--bg-color);
        color: var(--fg-color);
    }

    .cell {
        display: none;
    }

    .editor-default,
    .editor-fullheight {
        display: inline !important;
    }

    .acs-side-bar,
    .space-tools-section {
        background-color: var(--dracu-current);
    }

    body {
        color: var(--fg-color);
    }

    .aui-header .aui-dropdown2-trigger, 
    #header .aui-header .aui-nav-link, 
    #header .aui-header .aui-nav-imagelink, 
    #header .aui-header a,
    .acs-nav-sections p,
    .page-metadata-item a span {
        color: var(--fg-color);
    }

    .panel {
        color: var(--fg-color);
        border: 2px var(--dracu-purple);
    }

    .acs-nav-list .acs-nav-item-label {
        color: var(--fg-color);
    }

    .space-favourite {
        color: var(--dracu-orange);
    }

    .aui-dropdown2.aui-style-default {
        background-color: var(--bg-color);
    }

    #header .aui-header a:hover, 
    .aui-dropdown2-in-header.aui-style-default a:hover, 
    .aui-header .aui-dropdown2-trigger.active,
    .aui-dropdown2.aui-style-default:hover a:not(.aui-dropdown2-disabled):focus {
        background-color: var(--dracu-orange) !important;
        color: var(--bg-color) !important;
    }

    .acs-side-bar .current-item .acs-nav-item-link,
    li.acs-nav-item .acs-nav-item-link:focus {
        background-color: var(--dracu-selection);
    }

    li.acs-nav-item .acs-nav-item-link:hover {
        background-color: var(--dracu-comment);
        color: var(--dracu-orange);
    }

    .acs-nav-item.profile .icon,
    .acs-side-bar .acs-side-bar-content .acs-nav-list .icon {
        background-color: var(--fg-color);
    }

    #header .aui-header .aui-nav .aui-button-primary {
        background-color: var(--dracu-yellow);
        color: var(--bg-color) !important;
    }

    .wiki-content a, 
    .wiki-content a:link, 
    a,
    a:focus,
    #likes-section, 
    .label-list, 
    .aui-button .aui-icon, 
    .aui-button.aui-button-subtle,
    #likes-section .aui-iconfont-like, 
    content .acs-nav-list .icon {
        color: var(--dracu-purple) !important;
    }

    #title-text a, h2, h1, h5 {
        color: var(--dracu-pink) !important;
    }

    #title-text a {
        color: var(--dracu-pink);
        font-weight: 800;
    }

    .page-metadata, .page-metadata ul li a:link {
        color: var(--fg-color);
    }

    .quick-comment-prompt,
    body.mceContentBody {
        background-color: var(--dracu-selection);
        color: var(--fg-color);
        border: 0px;
    }

    #rte-toolbar {
        background-color: var(--dracu-selection);
        border-bottom: 0px;
    }

    #rte-savebar {
        border-top: 0px;
    }

    .aui-button.aui-button-primary {
        background-color: var(--dracu-orange);
    }

    #comments-section .comment-body form .editor-container {
        border: 0px;
    }

    .confluence-information-macro-warning, 
    .confluence-information-macro-tip {
        background-color: var(--dracu-yellow);
        border: 0px;
    }
}

